import React, { useState } from "react";
import axios from "axios";

function App() {
  const [productFile, setProductFile] = useState(null);
  const [brandTone, setBrandTone] = useState("");
  const [loading, setLoading] = useState(false);
  const [preview, setPreview] = useState([]);
  const [outputFile, setOutputFile] = useState("");

  const handleFileChange = (e) => {
    setProductFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!productFile) {
      alert("Please select a product file.");
      return;
    }
    setLoading(true);
    setPreview([]);
    setOutputFile("");
    const formData = new FormData();
    formData.append("product_file", productFile);
    formData.append("brand_tone", brandTone);

    try {
      const res = await axios.post(
        "http://localhost:8000/generate-descriptions",
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );
      if (res.data.status === "success") {
        setPreview(res.data.preview || []);
        setOutputFile(res.data.output_file);
      } else {
        alert(res.data.message || "An error occurred.");
      }
    } catch (err) {
      alert("Error connecting to backend.");
    }
    setLoading(false);
  };

  return (
    <div style={{ maxWidth: 600, margin: "40px auto", padding: 24, border: "1px solid #eee", borderRadius: 12 }}>
      <h2>SEO Product Description Generator</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Product File (.csv or .xlsx):<br />
            <input type="file" accept=".csv,.xlsx,.xls" onChange={handleFileChange} required />
          </label>
        </div>
        <div style={{ marginTop: 12 }}>
          <label>
            Brand Tone (optional):<br />
            <input
              type="text"
              value={brandTone}
              onChange={e => setBrandTone(e.target.value)}
              placeholder="e.g. Elegant, Playful"
              style={{ width: "100%" }}
            />
          </label>
        </div>
        <button type="submit" style={{ marginTop: 16, padding: "8px 24px" }} disabled={loading}>
          {loading ? "Generating..." : "Generate Descriptions"}
        </button>
      </form>

      {outputFile && (
        <div style={{ marginTop: 24 }}>
          <b>Output CSV saved at:</b>
          <div style={{ wordBreak: "break-all", color: "#2e7d32" }}>{outputFile}</div>
        </div>
      )}

      {preview.length > 0 && (
        <div style={{ marginTop: 24 }}>
          <h3>Preview (first 5 rows):</h3>
          <table border="1" cellPadding="6" style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                {Object.keys(preview[0]).map((k) => (
                  <th key={k}>{k}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {preview.map((row, i) => (
                <tr key={i}>
                  {Object.values(row).map((v, j) => (
                    <td key={j}>{v}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
